-- ----------------------------
-- Table structure for sys_msg
-- ----------------------------
DROP TABLE IF EXISTS `sys_msg`;
CREATE TABLE `sys_msg` (
  `id` bigint(20) NOT NULL COMMENT 'id',
  `title` varchar(200) DEFAULT NULL COMMENT '消息标题',
  `content` text COMMENT '内容',
  `protected_flag` tinyint(4) NOT NULL COMMENT '锁定型',
  `type` varchar(100) DEFAULT '1' COMMENT '消息类型',
  `level` tinyint(4) DEFAULT '1' COMMENT '消息级别',
  `url` varchar(200) DEFAULT NULL COMMENT '业务路由url',
  `receiver_type` tinyint(4) DEFAULT NULL COMMENT '接收者类型',
  `receiver_ids` text COMMENT '接收者',
  `sender` varchar(64) DEFAULT NULL COMMENT '发送者签名',
  `send_date` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '预计推送时间',
  `valid_date` datetime DEFAULT NULL COMMENT '过期时间',
  `remark` varchar(200) DEFAULT NULL COMMENT '备注',
  `creator` varchar(64) DEFAULT NULL COMMENT '创建者',
  `create_date` datetime DEFAULT NULL COMMENT '创建时间',
  `updater` varchar(64) DEFAULT NULL COMMENT '更新者',
  `update_date` datetime DEFAULT NULL COMMENT '更新时间',
  `should_nums` decimal(10,0) DEFAULT NULL COMMENT '应读',
  `read_nums` decimal(10,0) DEFAULT NULL COMMENT '已读',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统消息表';

